import { motion } from 'framer-motion';
import classes from './Hero.scss';
const textAnimation = {
  hidden: {
    x: 100,
    opacity: 0,
  },
  visible: custom => ({
    x: 0,
    opacity: 1,
    transition: { delay: custom * 2 }, 
  }),
}


const Hero = () => {
  return (
    <motion.section
      initial="hidden"
      whileInView="visible"
      className={`${classes.hero} container`}
    >
    
      <article className={classes.text}>
      <motion.h1 custom={4} variants={textAnimation} className={classes.title}>More than just shorter links</motion.h1>
        <motion.span custom={1} variants={textAnimation} className={classes.description}>Technology</motion.span>
        <motion.span custom={2} variants={textAnimation} className={classes.description}>+</motion.span>
        <motion.span custom={3} variants={textAnimation} className={classes.description}>Creativity</motion.span>
      </article>
    </motion.section>
  )
}

export { Hero };
