import { motion } from 'framer-motion';
import classes from './Hero.module.scss';
import Img from 'assets/img';

const textAnimationright = {
  hidden: {
    x: -100,
    opacity: 0,
  },
  visible: custom => ({
    x: 0,
    opacity: 1,
    transition: { delay: custom * 1.2 }, 
  }),
}

const textAnimationleft = {
    hidden: {
      x: 100,
      opacity: 0,
    },
    visible: custom => ({
      x: 0,
      opacity: 1,
      transition: { delay: custom * 1.2 }, 
    }),
  }

  const textAnimationup = {
    hidden: {
      y: 100,
      opacity: 0,
    },
    visible: custom => ({
      y: 0,
      opacity: 1,
      transition: { delay: custom * 1.2 }, 
    }),
  }

const Hero = () => {
  return (
    <motion.section
      initial="hidden"
      whileInView="visible"
      className={`${classes.hero} container-fluid g-0`}
    >
      <div className={classes.imgContainer}>
        <img src={Img} alt="hero" className={classes.img} />
      </div>

      <article className={classes.textup} >
        <motion.h1 custom={1} variants={textAnimationright} className={classes.title}>Technology</motion.h1>
        <motion.h1 custom={2} variants={textAnimationup} className={classes.title2}> +</motion.h1>
      </article>
      <article className={classes.textdown} >
        <motion.h1 custom={3} variants={textAnimationleft} className={classes.title3}>Creativity</motion.h1>
        <motion.h1 custom={3} variants={textAnimationleft} className={classes.title3}>Creativity</motion.h1>
      </article>
      <article className={classes.textdown} >
        <motion.h1 custom={4} variants={textAnimationup} className={classes.title4}>Get the edge with innovative product development, high-end web design, and effective development.</motion.h1>
      </article>
    </motion.section>
  )
}

export { Hero };
