import { motion } from 'framer-motion';
import classes from './Hero.module.scss';
const textAnimationright = {
  hidden: {
    x: -100,
    opacity: 0,
  },
  visible: custom => ({
    x: 0,
    opacity: 1,
    transition: { delay: custom * 1.2 }, 
  }),
}

const textAnimationleft = {
    hidden: {
      x: 100,
      opacity: 0,
    },
    visible: custom => ({
      x: 0,
      opacity: 1,
      transition: { delay: custom * 1.2 }, 
    }),
  }


const Hero = () => {
  return (
    <motion.section
      initial="hidden"
      whileInView="visible"
      className={`${classes.hero} container`}
    >
      <article className={classes.text}>
        <motion.h1 custom={1} variants={textAnimationright} className={classes.title}>Technology</motion.h1>
        <motion.h1 custom={2} variants={textAnimationleft} className={classes.title}>Creativity</motion.h1>
        <motion.p custom={2} variants={textAnimationright} className={classes.description}>Build your brand's recognition and get detailed insights on how your links are performing.</motion.p>
      </article>
    </motion.section>
  )
}

export { Hero };
