import { motion } from 'framer-motion';
import classes from './About.module.scss';


const textAnimationright = {
  hidden: {
    x: -100,
    opacity: 0,
  },
  visible: custom => ({
    x: 0,
    opacity: 1,
    transition: { delay: custom * 1.2 }, 
  }),
}

const textAnimationleft = {
    hidden: {
      x: 100,
      opacity: 0,
    },
    visible: custom => ({
      x: 0,
      opacity: 1,
      transition: { delay: custom * 1.2 }, 
    }),
  }

  const textAnimationup = {
    hidden: {
      y: 100,
      opacity: 0,
    },
    visible: custom => ({
      y: 0,
      opacity: 1,
      transition: { delay: custom * 1.2 }, 
    }),
  }

const About = () => {
  return (
    <motion.section
      initial="hidden"
      whileInView="visible"
      className={`${classes.hero} container-fluid g-0`}
    >
     

      <article className={classes.textup} >
        <motion.h1 custom={1} variants={textAnimationright} className={classes.title}>Technology</motion.h1>
        <motion.h1 custom={1} variants={textAnimationright} className={classes.title2}> +</motion.h1>
      </article>
      
      <article className={classes.textdownOne}>
       
        <motion.h1 custom={1} variants={textAnimationleft} className={classes.title3}>Creativity</motion.h1>
      </article>
      <article className={classes.textdownTwo}>
        <motion.div custom={4} variants={textAnimationup}className={`${classes.bottomContainer}`}>
          <div>&#9660;</div>
        </motion.div>
        <motion.h1 custom={3} variants={textAnimationup} className={classes.title4}>Get the edge with innovative product development, high-end web design, and effective development.</motion.h1>
      </article>
      
    </motion.section>
  )
}

export { About };
