import { useEffect } from 'react';
import OverlayScrollbars from 'overlayscrollbars';

const config = {
  scrollbars: {
    visibility: "auto",
    autoHide: "never",
  }
};

const useScrollbar = (root, hasScroll) => {
  useEffect(() => {
    let scrollbars;

    if (root.current && hasScroll) {
      scrollbars = OverlayScrollbars(document.querySelectorAll('body'), { });
    }

    return () => {
      if (scrollbars) {
        scrollbars.destroy();
      }
    }
  }, [root, hasScroll]);
};

export { useScrollbar };
